			          })
			          .catch(res => {
			            if (res.is_ready) {
			              res.wx.updateAppMessageShareData(configTimeline);
			              res.wx.updateTimelineShareData(configTimeline);
						  res.wx.onMenuShareAppMessage(configTimeline);
